import React, { useState } from "react";
import axios from "axios";
import { Image, ActivityIndicator, Alert, Platform } from "react-native";
import styled from "styled-components/native";
import useInput from "../../hooks/useInput";
import styles from "../../styles";
import constants from "../../Constants";
import { gql } from "apollo-boost";
import { useMutation } from "react-apollo-hooks";
import { FEED_QUERY } from "../home/Home";
import { ME } from "../tabs/Profile";

const UPLOADSTORY = gql`
  mutation uploadStory($caption: String, $files: [String!]!, $tagUser: [String!]) {
    uploadStory(caption: $caption, files: $files, tagUser: $tagUser) {
      id
      caption
    }
  }
`;

const View = styled.View`
  flex: 1;
`;

const Container = styled.View`
  padding: 20px;
  flex-direction: row;
`;

const Form = styled.View`
  justify-content: flex-start;
`;

const STextInput = styled.TextInput`
  margin-bottom: 10px;
  border: 0px solid ${styles.lightGreyColor};
  border-bottom-width: 1px;
  padding-bottom: 10px;
  width: ${constants.width - 180};
`;

const Button = styled.TouchableOpacity`
  background-color: ${props => props.theme.blueColor};
  padding: 10px;
  border-radius: 4px;
  align-items: center;
  justify-content: center;
`;

// const Button = styled.TouchableOpacity`
//   background-color: ${props => props.theme.blueColor};
//   padding: 10px;
//   border-radius: 4px;
//   align-items: center;
//   justify-content: center;
// `;

const Text = styled.Text`
  color: white;
  font-weight: 600;
`;

export default ({ navigation }) => {
  const [loading, setIsLoading] = useState(false);
  const photo = navigation.getParam("photo");
  const story = navigation.getParam("story");
  const uri = navigation.getParam("uri");

  const captionInput = useInput();
  const tagUserInput = useInput();

  const [uploadMutation] = useMutation(UPLOADSTORY, {
    refetchQueries: () => [{ query: FEED_QUERY }, { query: ME }]
  });

  let imageType // ì‚¬ì§„ì¸ì§€ ë¹„ë””ì˜¤ì¸ì§€ ì •í•˜ëŠ” ë³€ìˆ˜
  let uploadUri // ë¹„ë””ì˜¤ëŠ” ì‚¬ì§„ê³¼ ê°€ì ¸ì˜¤ëŠ” uriê°€ ë‹¬ë¼ì„œ êµ¬ë¶„í•´ ì¤˜ì•¼í•¨.
  let imgUri // ì›ì•ˆì— ë“¤ì–´ê°ˆë•Œ ëœ¨ëŠ” ì‚¬ì§„
  let name // íŒŒì¼ëª…
  let tagUsers // íƒœê·¸í•  ì‚¬ëŒë“¤ ë„£ì„ ë³€ìˆ˜

  if (navigation.getParam("photo")) {
    imgUri = photo.uri
    name = photo.filename;
    const [, type] = name.split(".");
    uploadUri = photo.uri
    imageType = Platform.os === "ios" ? type.toLowerCase() : "image/jpeg";
  } else if (navigation.getParam("story")) {
    imgUri = story.uri
    name = story.filename;
    uploadUri = uri
    imageType = "mp4";
  }
  const handleSubmit = async () => {
    const formData = new FormData();
    formData.append("file", {
      name,
      type: imageType,
      uri: uploadUri,
    });

    try {
      setIsLoading(true);
      const { data: { location } } = await axios.post("https://semicolon-backend.herokuapp.com/api/upload", formData, {
        headers: { 'Content-Type': 'multipart/form-data', }
      });
      console.log(location);

      //console.log(tagUserInput.value);

      // if (tagUserInput.value != "" || tagUserInput.value != "undefined" ) {
      //   tagUsers = tagUserInput.value.split(" ");
      // }

      if (tagUserInput.value) {
         tagUsers = tagUserInput.value.split(" ");
      }
      else {
        tagUsers = "";
      }

      await uploadMutation({
        variables: {
          files: [location],
          caption: captionInput.value,
          tagUser: tagUsers
        }
      });

      navigation.navigate("TabNavigation");

    } catch (e) {
      console.log("ì—ëŸ¬ " + e);
      Alert.alert("ì—…ë¡œë“œ ì‹¤íŒ¨", "ë‹¤ì‹œ ì‹œë„í•´ ì£¼ì„¸ìš” ğŸ¤”");
    } finally {
      setIsLoading(false);
    }

  };
  return (
    <View>
      <Container>
        <Image
          source={{ uri: imgUri }}
          style={{ height: 80, width: 80, marginRight: 30 }}
        />
        <Form>
          <STextInput
            onChangeText={captionInput.onChange}
            value={captionInput.value}
            placeholder="ê¸€ ë‚´ìš©"
            multiline={true}
            placeholderTextColor={styles.darkGreyColor}
          />
          <STextInput
            onChangeText={tagUserInput.onChange}
            value={tagUserInput.value}
            placeholder="íƒœê·¸í•  ì‚¬ëŒ"
            multiline={true}
            placeholderTextColor={styles.darkGreyColor}
          />
          <Button onPress={handleSubmit}>
            {loading ? (
              <ActivityIndicator color="white" />
            ) : (
                <Text>ì—…ë¡œë“œ </Text>
              )}
          </Button>
        </Form>
      </Container>
    </View>
  );
};
